import {useState} from "react";
import Fab from './fab';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faArrowDown} from '@fortawesome/free-solid-svg-icons/faArrowDown';

const Question = (props) => {

    const [slideDown, setSlideDown] = useState(false);


    function slider() {

        if (slideDown) {
            return {
                maxHeight: "100vh"
            }
        }


    }


    return (
        <>
            <div className={"d-flex flex-row "}>
                <h2 className={"text-muted"} onClick={() => setSlideDown(!slideDown)}
                    style={{cursor: "pointer"}}>{props.title}</h2>
                <Fab variant='primary' onClick={() => setSlideDown(!slideDown)}>
                    <FontAwesomeIcon icon={faArrowDown}/>
                </Fab>
            </div>
            <p className="lead text-right px-2 transtional-slider-down d-flex align-items-center"
               style={{...slider(), overflow: "hidden"}}>{props.answear}</p>
            <hr/>
        </>
    )
};

export default Question;
