import {useState} from "react";

const Question = (props) => {

    const [slideDown, setSlideDown] = useState(false);


    function slider() {

        if (slideDown) {
            return {
                height: "fit-content"
            };

            return {
                height: 0
            }
        }

    }


    return (
        <>
            <div className={"row"} style={{cursor: "pointer"}} onClick={() => setSlideDown(!slideDown)}
                 style={slider()}>

                <div className="col-6 text-right">
                    <h2>{props.title}</h2>
                </div>

                <div className={"icon-container col-1 mr-auto"}>
                    >>
                </div>
            </div>
            <p className="lead text-right px-3">{props.answear}</p>
            <hr/>
        </>
    )
};

export default Question;
