import { useState } from "react";
import Fab from "./fab";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowDown } from "@fortawesome/free-solid-svg-icons/faArrowDown";

const Question = props => {
  const [slideDown, setSlideDown] = useState(false);

  function slider() {
    if (slideDown) {
      return {
        maxHeight: "100vh"
      };
    }
  }

  return (
    <section className={"shadow-lg py-4 px-5"}>
      <div className={"d-flex flex-row  justify-content-between"}>
        <h2
          className={"text-muted text-right"}
          onClick={() => setSlideDown(!slideDown)}
          style={{ cursor: "pointer" }}
        >
          {props.title}
        </h2>
        <button style={{backgroundColor:'#D1D1D1',borderRadius:"50%",height:'2.75rem'}}>
          <FontAwesomeIcon icon={faArrowDown} />
        </button>
      </div>
      <p
        className="lead text-right px-2 transtional-slider-down d-flex align-items-center"
        style={{ ...slider(), overflow: "hidden" }}
      >
        {props.answear}
      </p>
      <hr />
    </section>
  );
};

export default Question;
