import {Alert} from 'react-bootstrap'
import {useEffect, useState} from "react";
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';
import {faExclamationCircle} from '@fortawesome/free-solid-svg-icons/faExclamationCircle';
import {faTimes} from '@fortawesome/free-solid-svg-icons/faTimes';

const NagdaAlert = (props) => {
    const [show, setShow] = useState(true);

    function display() {
        if (show) {
            return 'block';
        }

        return 'none' + ' !important';
    }

    useEffect(() => {


    }, [show]);
    return (
        <div
            className={`text-center d-flex flex-row position-absolute alert alert-${props.variant}`} role="alert"
            style={{
                width: props.width,
                height: props.height,
                bottom: 0,
                left: "26%",
                display: display()
            }}>
            <FontAwesomeIcon icon={faExclamationCircle} className="fa-2x ml-3"/>
            <small>
                من اجل تجربة مستخدم وامان افضل نحيط علمكم بأننا نستخدم الكوكيز وهذا اعلان منا بذلك احتراما لخصوصيتكم
            </small>
            <button className="btn btn-warning mr-4" onClick={() => setShow(false)}>
                موافق
            </button>
        </div>
    )
};


export default NagdaAlert;
