import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';
import {faDotCircle} from '@fortawesome/free-solid-svg-icons/faDotCircle';
import {ScrollTo} from 'react-scroll-to';
import {createRef} from 'react';

export function SimpleNavigator(props) {


    return (
        <section className="position-fixed" style={{zIndex: 566, top: '50%', left: '2em'}}>
            <ul className="list-unstyled list-group p-0">
                {props.children}
            </ul>
        </section>
    )
};

export function SimpleNavigatorItem(props) {

    const scrollRef = createRef();


    return (
        <li>
            <ScrollTo>
                {({scrollTo}) => (
                    <a style={{cursor: "pointer"}} className="link-hvr" onClick={() => scrollTo({ref: scrollRef})}>
                        <FontAwesomeIcon icon={faDotCircle} style={{color: "#D18C08"}}/>
                    </a>
                )}
            </ScrollTo>
        </li>
    )
};
