import {useState} from "react";
import Fab from './fab';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faArrowDown} from '@fortawesome/free-solid-svg-icons/faArrowDown';

const Question = (props) => {

    const [slideDown, setSlideDown] = useState(false);


    function slider() {

        if (!slideDown) {
            return {
                height: '0'
            };
        }

        return {
            height: "75px"
        }
    }


    return (
        <>
            <div className={"row"} style={{cursor: "pointer"}} onClick={() => setSlideDown(!slideDown)}>

                <div className="col-6 text-right">
                    <h2>{props.title}</h2>
                </div>

                <div className={"icon-container col-1 mr-auto"}>
                    <Fab variant='warning' className="mt-2">
                        <FontAwesomeIcon icon={faArrowDown}/>
                    </Fab>
                </div>
            </div>
            <p className="lead text-right px-3 transtional-slider-down d-flex align-items-center"
               style={{...slider(), overflow: "hidden"}}>{props.answear}</p>
            <hr/>
        </>
    )
};

export default Question;
